// https://www.youtube.com/watch?v=X392f_AxvVQ
import React, { useState, useEffect } from 'react'
import { Avatar, Button, Paper, Grid, Typography, Container } from "@material-ui/core";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import { useDispatch } from 'react-redux'
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from "react-router-dom";
import useStyles from "./styles";
import Input from "./Input"
import Icon from "./Icon";
import { GoogleOAuthProvider } from "@react-oauth/google"
import jwt_decode from "jwt-decode";

const initialState = { firstName: "", lastName: "", email: "", password: "", confirmPassword: "" };
const Auth = () => {
    console.log("hello hello hello")
    const classes = useStyles();
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const [isSignup, setIsSignup] = useState(false);
    const [formData, setFormData] = useState({
        firstName: "",
        lastName: "",
        email: "",
        password: "",
        confirmPassword: ""
    })


    const handleSubmit = () => {

    }
    const handleChange = () => {
        console.log("change");
    }
    const googleSuccess = async (res) => {
        console.log("res", res)
        try {
            const decode = jwt_decode(res.credential)
            console.log("decode=========", decode)
            // dispatch({ type: 'Auth', data: { token: decode } });
            // console.log("option", dispatch({ type: 'Auth', Data: { token: decode } }))

            const setLocal = localStorage.setItem("profile", JSON.stringify({ token: decode }));
            console.log("setLocal", setLocal)
            navigate("/");
        } catch (error) {
            console.log(error);
        }
    }
    const googleFailure = () => {
        console.log("Google Sign In was unsuccessful. Try again later");
    }
    const handleShowPassword = () => setShowPassword((prevShowPassword) => !prevShowPassword);

    const switchMode = () => {
        setIsSignup((prevIsSignup) => !prevIsSignup)
        handleShowPassword(false);
    }

    return (
        <GoogleOAuthProvider clientId="1098638947980-v20v8326tcd9ctdrcjhr9r5nj62408fa.apps.googleusercontent.com">
            <Container component="main" maxWidth="xs" >
                <Paper className={classes.paper} elevation={3}>
                    <Avatar className={classes.avatar}>
                        <LockOutlinedIcon />
                    </Avatar>
                    <Typography variant="h5">
                        {isSignup ? "Sign up" : "Sign in"}
                    </Typography>
                    <form className={classes.form} onSubmit={handleSubmit}>
                        <Grid container spacing={1}>
                            {isSignup && (
                                <>
                                    <Input name="firstName" label="First Name" handleChange={handleChange} autoFocus half />
                                    <Input name="lastName" label="Last Name" handleChange={handleChange} half />
                                </>
                            )}
                            <Input name="email" label="Email Address" handleChange={handleChange} autoFocus />
                            <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? "text" : "password"} handleShowPassword={handleShowPassword} />
                            {isSignup && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password" />}
                        </Grid>
                        <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
                            {isSignup ? "Sign up" : "Sign in"}
                        </Button>
                        <GoogleLogin
                            render={(renderProps) => (
                                <Button color="primary"
                                    className={classes.googleButton}
                                    onClick={renderProps.onClick}
                                    disabled={renderProps.disabled}
                                    fullWidth startIcon={<Icon />}
                                    variant="contained"
                                >
                                    Google Sign in
                                </Button>
                            )}
                            onSuccess={googleSuccess}
                            onFailure={googleFailure}
                        />

                        <Grid container justify="flex-end">
                            <Grid item>
                                <Button onClick={switchMode}>
                                    {isSignup ? "Already have an account" : "Dont have an account"}?
                                </Button>
                            </Grid>
                        </Grid>
                    </form>
                </Paper>

            </Container>
        </GoogleOAuthProvider>
    );
}
export default Auth;
