import { FETCH_ALL, CREATE, UPDATE, DELETE, LIKE } from '../constants/actionTypes';


export default (posts = [], action) => {

console.log("actionPayload",action.payload)
    switch (action.type) {
        case FETCH_ALL:
            return action.payload;
        case 'CREATE':
            return [posts, action.payload];
        default:
            return posts;
    }
}
