import axios from "axios";

const url = "http://localhost:5000/post";

export const fetchPosts = () => axios.get(url);
export const likePost = (id) => axios.patch(`${url}/${id}/likePost`);
export const putPost = () => axios.post(url)
export const createPost = (newPost) => axios.post(url, newPost);
export const updatePost = (id, updatedPost) => axios.put(`${url}/${id}`, updatedPost);
export const deletePost = (id) => axios.delete(`${url}/${id}`);
export const fetchPost = (id) => axios.get(`${url}/${id}`);
export const fetchPostsByUser = (userId) => axios.get(`${url}/user/${userId}`);
