import React, { useState, useEffect } from 'react';
import useStyle from "./style";
import { TextField, Button, Typography, Paper } from "@material-ui/core"
import FileBase from "react-file-base64";
import { useDispatch, useSelector } from 'react-redux';
// import { createPost } from "../actions/post";
import { createPost, updatePost } from "../../actions/post"
const Form = ({ currentId, setCurrentId }) => {

    const post = useSelector((state) => currentId ? state.posts.find(post => post._id === currentId) : null)
    console.log("post", post)

    const styles = useStyle();
    const dispatch = useDispatch();
    const [postData, setPostData] = useState({
        creator: '',
        title: '',
        message: '',
        tags: '',
        selectedFile: '',
        comments: []
    })
    useEffect(() => {
        if (post) {
            setPostData(post)
        }
    }, [post])

    const handleSubmit = (e) => {
        e.preventDefault();
        if (currentId) {
            dispatch(updatePost(postData))
        }
        else {
            dispatch(createPost(postData))
        }
    }
    let clear = () => {
        setPostData({
            creator: '',
            title: '',
            message: '',
            tags: '',
            selectedFile: '',
            comments: []
        })
    }
    return (
        <div>
            <Paper className={styles.paper}>
                <form autoComplete="off" noValidate className={`${styles.root} ${styles.form}`} onSubmit={handleSubmit}>
                    <Typography variant="h5" component="h1" className={styles.title}>
                        Create A memeory
                    </Typography>
                    <TextField name="creator"
                        variant="outlined"
                        label="creator"
                        fullWidth
                        value={postData.creator}
                        onChange={(e) => setPostData({ ...postData, creator: e.target.value })}
                    />

                    <TextField name="title"
                        variant="outlined"
                        label="title"
                        fullWidth
                        value={postData.title}

                        onChange={(e) => setPostData({ ...postData, title: e.target.value })}
                    />
                    <TextField name="message"
                        variant="outlined"
                        label="message"
                        fullWidth
                        value={postData.message}
                        onChange={(e) => setPostData({ ...postData, message: e.target.value })}
                    />
                    <TextField name="tags"
                        variant="outlined"
                        label="tags"
                        fullWidth
                        value={postData.tags}
                        onChange={(e) => setPostData({ ...postData, tags: e.target.value })}
                    />
                    <div className={styles.fileInput}>
                        <FileBase
                            type="file"
                            multiple={false}
                            onDone={(file) => setPostData({ ...postData, selectedFile: file.base64 })}
                        />
                    </div>
                    <Button variant="contained" size="large" color="primary" type="submit" className={styles.buttonSubmit} fullWidth>
                        Submit
                    </Button>
                    <Button variant="contained" size="small" color="secondary" fullWidth onClick={clear}>
                        Clear
                    </Button>
                </form>
            </Paper>
        </div>
    );
}

export default Form;
