const url = "http://localhost:5000/post";

export const fetchPosts = () => axios.get(url);
export const createPost = (newPost) => axios.post(`${url}/createPost`, newPost);
axios.post('/formulas/create', {
	name: "",
	parts: ""
})
.then(response => { 
